import React, { useEffect, useState } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from "react-infinite-scroll-component";


const News =(props)=> {
      const [articles, setArticles] = useState([]);
      const [loading, setLoading] = useState(true);
      const [page, setPage] = useState(1);
      const [totalResults, setTotalResults] = useState(0);
      // document.title = `${capitalizeFirstLetter(props.category)} - NewsApp`;

const capitalizeFirstLetter = (string)=>{
        return string.charAt(0).toUpperCase()+string.slice(1);
      }

  

  const updateNews = async()=> {
   props.setProgress(10)
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&category=${props.category}&apiKey=31b5dd8d5a5740a6ad8e4a0957b4f950&page=${page+1}&pageSize=${props.pageSize}`
    
    
    setLoading(true)
    let data = await fetch(url);
    props.setProgress(30)
    let parsedData = await data.json()
    props.setProgress(70)
   
    setArticles(parsedData.articles)
    setTotalResults(parsedData.totalResults)
    setLoading(false)

   props.setProgress(100);
  }

  useEffect(()=>{
    this.updateNews();
  }, []);

  
 const fetchMoreData = async() => {
  
  let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=31b5dd8d5a5740a6ad8e4a0957b4f950&page=${page+1}&pageSize=${props.pageSize}`
  setPage(page+1) 
    
    let data = await fetch(url);
    let parsedData = await data.json()
    setArticles(articles.concat(parsedData.articles))
    setTotalResults(parsedData.totalResults)
     
  };  

 
    return (
    
     <>
        <h1 className='text-center my-3'>NewsApp Top {capitalizeFirstLetter(props.category)} HeadLines </h1>
          {loading &&<Spinner/>}        

          <InfiniteScroll
          dataLength={articles.length}
          next={fetchMoreData}
          hasMore={articles.length !== totalResults}
          loader={<Spinner/>}
        >
        
      <div className="container">
         
        <div className='row'>
          {articles.map((element) => {
            return <div className="col-md-3" key= 
             {element.url}>
              <NewsItem title={element.title?element.title:""} description={element.description?element.description:""}imageUrl={element.urlToImage}newsUrl={element.url} author={element.author} date={element.publishedAt} />
            </div>
          })}

        </div>
        </div>
        </InfiniteScroll>
        
        </>
    )
  
}

News.defaultProps = {
  country: 'in',
  pageSize: 8,
  category: 'general'
}

News.defaultPropTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string
}

export default News;
