import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
  

    constructor(){
      super();
      console.log("Hello i am constructor of newsitem");
      this.state ={
        articles : [],
        loading : false,
        page:1
      }
    }

    async componentDidMount(){
      let url ="https://newsapi.org/v2/top-headlines?country=in&apiKey=31b5dd8d5a5740a6ad8e4a0957b4f950&page=1" 
      let data = await fetch(url);
      let parsedData = await data.json()
      console.log(parsedData);
      this.setState({articles: parsedData.articles})
     
    }

    handlePrevClick = ()=>{
      console.log("click previous");
    }

    handleNextClick = ()=>{
      console.log('Click next');
    }
    
  render() {
    return (
      <div className='container my-3'>
            <h2>News Top HeadLines</h2>
            <div className='row'>
              {this.state.articles.map((element)=>{
                return <div className="col-md-3" key={element.url}>
                           <NewsItem title={element.title?element.title.slice(0,46):""} description={element.description?element.description.slice(0,88):""} imageUrl={element.urlToImage} newsUrl={element.url}/>
                       </div> 
              })}
              
            </div>
            <div className="container d-flex justify-content-between">
            <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}> &larr; previous</button>
            <button disabled={this.state.page>=1} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &larr; </button>
            </div>
      </div>
    )
  }
}

export default News
